PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?event ?eventName ?eventType ?date ?location ?hasParticipatedTo ?training ?frequency ?intensity
WHERE {
  ?event rdf:type horses:SportingEvent .
  ?event horses:hasName ?eventName .
  ?event rdf:type ?eventType .
  FILTER(?eventName = "Event_SJ_01")
  OPTIONAL { ?event horses:hasDate ?date . }
  OPTIONAL { ?event horses:hasLocation ?location . }
  OPTIONAL { ?training rdf:type horses:Training .
            ?event horses:hasParticipatedTo ?training .
            OPTIONAL { ?training horses:Frequency ?frequency . }
            OPTIONAL { ?training horses:Intensity ?intensity . } }
}

