PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT ?horseName
WHERE {
  ?horse rdf:type horses:Horse .
  OPTIONAL { ?horse horses:hasName ?horseName . }
}

PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT ?race
WHERE {
  ?horse rdf:type horses:Horse .
  ?horse horses:hasName "Dakota" .
  OPTIONAL { ?horse horses:hasRace ?race . }
}

PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT ?race
WHERE {
  ?horse rdf:type horses:Horse .
  ?horse horses:hasName "Naya" .
  OPTIONAL { ?horse horses:hasRace ?race . }
}

PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT ?event ?eventName
WHERE {
  ?horse rdf:type horses:Horse .
  ?horse horses:hasName "Dakota" .
  ?horse horses:CompetesIn ?event .
  OPTIONAL { ?event horses:hasName ?eventName . }
}

PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT ?training ?frequency ?intensity ?volume
WHERE {
  ?horse rdf:type horses:Horse .
  ?horse horses:hasName "Dakota" .
  ?horse horses:TrainsIn ?training .
  ?training rdf:type horses:Training .
  OPTIONAL { ?training horses:Frequency ?frequency . }
  OPTIONAL { ?training horses:Intensity ?intensity . }
  OPTIONAL { ?training horses:Volume ?volume . }
}

PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT (COUNT(DISTINCT ?rider) AS ?count)
WHERE {
  ?rider rdf:type horses:Rider .
}

PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>

