  horses:Event_SJ_01 horses:category ?category .
}

PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>

SELECT ?category
WHERE {
  horses:Event_Dressage_01 horses:category ?category .
}

PREFIX horses: <http://www.semanticweb.org/noamaadra/ontologies/2024/2/Horses#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>

SELECT ?stage ?frequency ?intensity ?volume
WHERE {
  ?stage rdf:type horses:PreparationStage ;
          horses:Frequency ?frequency ;
          horses:Intensity ?intensity ;
          horses:Volume ?volume .
}
UNION
{
  ?stage rdf:type horses:PreCompetitionStage ;
          horses:Frequency ?frequency ;
          horses:Intensity ?intensity ;
          horses:Volume ?volume .
}

